import "../assets/scss/productCard.scss";
import { Context } from "../Context";
import { useContext, useEffect, useRef, useState } from "react";

const ProductCard = ({ index, val }) => {
  const [, , cartItems, setCartItems, page] = useContext(Context),
    button = useRef(null),
    [width, setWidth] = useState(window.innerWidth),
    [clickCount, setClickCount] = useState(0);

  useEffect(() => {
    if (width < 800) {
      button.current.style.marginBottom = "0";
      button.current.classList.add("hovered");
    } else {
      button.current.classList.remove("hovered");
      button.current.style.marginBottom = "-50px";
    }
    window.addEventListener("resize", () => setWidth(window.innerWidth));
  }, [width]);

  const mouseover = () => {
    button.current.style.marginBottom = "0";
    button.current.classList.add("hovered");
  };

  const mouseleave = () => {
    if (width > 800) {
      button.current.classList.remove("hovered");
      button.current.style.marginBottom = "-50px";
    }
  };

  const addItem = () => {
    let items = [...page];
    // setClickCount(() => clickCount + 1)
    // if(clickCount % 2 === 1) {
    const itemName = button.current.id;
    console.log(itemName);
    const cartNames = [...cartItems].map((val) => val.name);
    items = items.splice(index, 1);
    if (!cartNames.includes(items[0].name)) {
      setCartItems([...cartItems, ...items]);
    }
    // } else {
    //     const cartNames = [...car]
    // }
  };

  return (
    <section className="product">
      <div
        className="product__img"
        onMouseOver={mouseover}
        onMouseLeave={mouseleave}
      >
        <img src={val.image.src} alt="product" />
        {val.bestseller ? <div className="caption">Best Seller</div> : ""}
        <button
          ref={button}
          className="add-to-cart"
          onClick={addItem}
          id={val.name}
        >
          ADD TO CART
        </button>
      </div>

      <section className="product__details">
        <div className="others">
          <div className="details">
            <p>{val.category}</p>
            <h3>{val.name}</h3>
            <p className="price">$ {val.price}</p>
          </div>
        </div>
      </section>
    </section>
  );
};

export default ProductCard;
